// frontend/src/TrainMap.js

// This is a React component that renders your upgraded SVG map with a proper siding.
const TrainMap = () => {
  return (
    <svg 
      width="100%" 
      height="250" 
      viewBox="0 0 1400 250" 
      xmlns="http://www.w3.org/2000/svg"
      style={{ backgroundColor: '#f8f9fa', border: '1px solid #dee2e6', borderRadius: '8px' }}
    >
      <title>Mumbai - Pune Railway Route</title>
      
      {/* Main Track Line */}
      <line 
        id="main-track" 
        x1="210" y1="151" 
        x2="1370" y2="151" 
        stroke="#666666" 
        strokeWidth="4" 
      />

      {/* NEW: Loop Track drawn with a path to look like a real siding */}
      <path
        id="loop-track-karjat"
        d="M 780 151 C 795 151, 800 121, 815 121 L 915 121 C 930 121, 935 151, 950 151"
        stroke="#888888"
        strokeWidth="3"
        fill="none"
        strokeDasharray="5,5"
      />

      {/* Stations */}
      {[
        { x: 210, name: "MUMBAI CST" }, { x: 360, name: "DADAR" },
        { x: 510, name: "THANE" }, { x: 660, name: "KALYAN" },
        { x: 810, name: "KARJAT" }, { x: 960, name: "LONAVALA" },
        { x: 1110, name: "TALEGAON" }, { x: 1310, name: "PUNE" }
      ].map(station => (
        <g key={station.name}>
          <ellipse cx={station.x} cy="151" rx="10" ry="10" fill="#1f77b4" stroke="#ffffff" strokeWidth="2" />
          <text x={station.x} y="185" fontFamily="Helvetica" fontSize="12px" fontWeight="bold" textAnchor="middle">{station.name}</text>
        </g>
      ))}

    </svg>
  );
};

export default TrainMap;
