import React from 'react';
import './Header.css';

function Header({ user, onProfile, onHistory, onLogout }) {
  return (
    <header className="app-header">
      <h1 className="app-title">ðŸš„ AI Train Traffic Control</h1>
      <div className="header-right">
        <span className="user-info">
          {user && <>Logged in as: <strong>{user.username}</strong> ({user.role})</>}
        </span>
        <button onClick={onProfile} className="header-btn">Profile</button>
        <button onClick={onHistory} className="header-btn">Decision History</button>
        <button onClick={onLogout} className="header-btn logout">Logout</button>
      </div>
    </header>
  );
}

export default Header;
