// frontend/src/components/AuthPage.js
import React, { useState } from "react";
import "./AuthPage.css";

function AuthPage({ onLoginSuccess }) {
  const [mode, setMode] = useState("login");
  const [formData, setFormData] = useState({ username: "", password: "", role: "employee" });

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`http://127.0.0.1:5001/api/${mode}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) onLoginSuccess(data.user);
        else alert(data.message);
      })
      .catch((err) => console.error("Error:", err));
  };

  return (
    <div
      className="auth-page"
      style={{
        background: `url(${process.env.PUBLIC_URL}/train-bg.jpg) no-repeat center center/cover`,
      }}
    >
      <div className="auth-form">
        <h2>{mode === "login" ? "Login" : "Register"}</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Operator ID"
            value={formData.username}
            onChange={(e) => setFormData({ ...formData, username: e.target.value })}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
            required
          />
          {mode === "register" && (
            <select
              value={formData.role}
              onChange={(e) => setFormData({ ...formData, role: e.target.value })}
            >
              <option value="employee">Employee</option>
              <option value="admin">Admin</option>
            </select>
          )}
          <button type="submit">
            {mode === "login" ? "Secure Login" : "Register"}
          </button>
        </form>
        <div
          className="toggle-mode"
          onClick={() => setMode(mode === "login" ? "register" : "login")}
        >
          {mode === "login"
            ? "Don't have an account? Register"
            : "Already have an account? Login"}
        </div>
      </div>
    </div>
  );
}

export default AuthPage;
