import React, { useState } from 'react';
import './AuthPage.css';

function AuthPage({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Please fill all fields.');
      return;
    }
    // âœ… Keep functionality same
    onLoginSuccess({ username, role });
  };

  return (
    <div className="auth-page">
      <div className="auth-overlay"></div>

      <div className="auth-box">
        <h2 className="auth-title">AI Train Traffic Control</h2>
        <p className="auth-subtitle">
          Authorized access for locomotive pilots and operational staff. Please log in to continue.
        </p>

        {error && <div className="auth-error">{error}</div>}

        <form className="auth-form" onSubmit={handleLogin}>
          <div className="input-group">
            <span className="input-icon">ðŸ‘¤</span>
            <input
              type="text"
              placeholder="Operator ID"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>

          <div className="input-group">
            <span className="input-icon">ðŸ”’</span>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <button type="submit" className="login-button">Secure Login</button>
        </form>

        <div className="auth-links">
          <a href="#">Forgot Password?</a>
          <br />
          Donâ€™t have an account? <a href="#">Request Access Credentials</a>
        </div>
      </div>
    </div>
  );
}

export default AuthPage;
