// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import TrainMap from './TrainMap';
import './App.css';

const getTrainIcon = (trainType) => {
  // ... (this function is unchanged)
  switch (trainType) {
    case 'EXPRESS': return 'ðŸš„';
    case 'GOODS': return 'ðŸš‚';
    default: return 'ðŸšƒ';
  }
};

function App() {
  const [simulationState, setSimulationState] = useState({ trains: [], simulation_time: "00:00:00" });

  useEffect(() => {
    const intervalId = setInterval(() => {
      fetch('http://127.0.0.1:5001/api/get_simulation_state')
        .then(response => response.json())
        .then(data => setSimulationState(data))
        .catch(error => console.error("Error fetching simulation state:", error));
    }, 2000); 
    return () => clearInterval(intervalId);
  }, []);

  // --- UPDATED to handle moving trains to the loop line ---
  const getTrainPosition = (train) => {
    const SVG_WIDTH = 1400;
    const TRACK_START_X = 210;
    const TRACK_END_X = 1310;
    const ROUTE_LENGTH_KM = 192;

    let percentageTraveled = train.position_km / ROUTE_LENGTH_KM;
    if (percentageTraveled > 1) percentageTraveled = 1;
    if (percentageTraveled < 0) percentageTraveled = 0;

    const trackWidthInSvg = TRACK_END_X - TRACK_START_X;
    const trainPositionInSvg = TRACK_START_X + (percentageTraveled * trackWidthInSvg);
    const finalPercentage = (trainPositionInSvg / SVG_WIDTH) * 100;

    // By default, train is on the main line
    let yPos = 151;
    // IF the train's status is HALTED_IN_LOOP, move it up to the loop line's y-position
    if (train.status === 'HALTED_IN_LOOP') {
      yPos = 121; // This 'y' coordinate must match the 'y' of the loop line in your SVG
    }

    return { left: `${finalPercentage}%`, top: `${yPos}px` };
  };

  return (
    <div className="App">
      <h1>AI Train Traffic Control</h1>
      <h2>Simulation Time: {simulationState.simulation_time}</h2>
      <div className="dashboard">
        <TrainMap />
        {simulationState.trains.map(train => {
          const position = getTrainPosition(train); // Pass the whole train object now
          return (
            <div 
              key={train.id} 
              className="train-icon" 
              style={{ left: position.left, top: position.top }}
              title={`${train.name} (${train.id}) - Status: ${train.status}`}
            >
              {getTrainIcon(train.type)}
              <div className="train-label">{train.id}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
