// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import TrainMap from './TrainMap';
import './App.css';

const getTrainIcon = (trainType) => {
  switch (trainType) {
    case 'EXPRESS': return 'ðŸš„';
    case 'GOODS': return 'ðŸš‚';
    default: return 'ðŸšƒ';
  }
};

function App() {
  const [simulationState, setSimulationState] = useState({ trains: [], simulation_time: "00:00:00" });

  useEffect(() => {
    const intervalId = setInterval(() => {
      fetch('http://127.0.0.1:5001/api/get_simulation_state')
        .then(response => response.json())
        .then(data => setSimulationState(data))
        .catch(error => console.error("Error fetching simulation state:", error));
    }, 2000); 
    return () => clearInterval(intervalId);
  }, []);

  // --- UPGRADED POSITIONING LOGIC FOR SMOOTH ANIMATION ---
  const getTrainPosition = (train) => {
    // --- Constants for mapping position to pixels ---
    const SVG_WIDTH = 1400;
    const TRACK_START_X = 210;
    const TRACK_END_X = 1310;
    const ROUTE_LENGTH_KM = 192;
    const MAIN_TRACK_Y = 151;
    const LOOP_TRACK_Y = 121;

    // --- Constants for the loop maneuver ---
    const LOOP_START_KM = 84.0;
    const LOOP_HALT_KM = 85.0;
    const LOOP_END_KM = 86.0;

    // --- Base Position Calculation (Horizontal) ---
    let percentageTraveled = train.position_km / ROUTE_LENGTH_KM;
    if (percentageTraveled > 1) percentageTraveled = 1;
    if (percentageTraveled < 0) percentageTraveled = 0;

    const trackWidthInSvg = TRACK_END_X - TRACK_START_X;
    const trainPositionInSvg = TRACK_START_X + (percentageTraveled * trackWidthInSvg);
    const finalXPercentage = (trainPositionInSvg / SVG_WIDTH) * 100;

    // --- Advanced Animation Calculation (Vertical) ---
    let finalYPos = MAIN_TRACK_Y;

    // If the train is in the process of the loop maneuver
    if (train.status === 'EN_ROUTE_TO_LOOP' || train.status === 'HALTED_IN_LOOP') {
      if (train.position_km < LOOP_START_KM) {
        // Still on the main track, approaching the loop
        finalYPos = MAIN_TRACK_Y;
      } else if (train.position_km >= LOOP_START_KM && train.position_km < LOOP_HALT_KM) {
        // Moving from main track up to the loop track (curving up)
        const maneuverProgress = (train.position_km - LOOP_START_KM) / (LOOP_HALT_KM - LOOP_START_KM);
        finalYPos = MAIN_TRACK_Y - (maneuverProgress * (MAIN_TRACK_Y - LOOP_TRACK_Y));
      } else {
        // Arrived at the loop and is waiting
        finalYPos = LOOP_TRACK_Y;
      }
    } else if (train.halted_by === null && train.position_km > LOOP_HALT_KM && train.position_km < LOOP_END_KM) {
      // It has been restarted and is now rejoining the main track (curving down)
      const maneuverProgress = (train.position_km - LOOP_HALT_KM) / (LOOP_END_KM - LOOP_HALT_KM);
      finalYPos = LOOP_TRACK_Y + (maneuverProgress * (MAIN_TRACK_Y - LOOP_TRACK_Y));
    }

    return { left: `${finalXPercentage}%`, top: `${finalYPos}px` };
  };

  return (
    <div className="App">
      <h1>AI Train Traffic Control</h1>
      <h2>Simulation Time: {simulationState.simulation_time}</h2>
      <div className="dashboard">
        <TrainMap />
        {simulationState.trains.map(train => {
          const position = getTrainPosition(train);
          return (
            <div 
              key={train.id} 
              className="train-icon" 
              style={{ left: position.left, top: position.top }}
              title={`${train.name} (${train.id}) - Status: ${train.status}`}
            >
              {getTrainIcon(train.type)}
              <div className="train-label">{train.id}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
