// frontend/src/App.js

import React, { useState, useEffect } from 'react';
import TrainMap from './TrainMap';
import './App.css';

// --- Helper function to choose an icon based on train type ---
const getTrainIcon = (trainType) => {
  switch (trainType) {
    case 'EXPRESS':
      return 'ðŸš„'; // High-speed train emoji
    case 'GOODS':
      return 'ðŸš‚'; // Steam locomotive emoji for goods trains
    default:
      return 'ðŸšƒ'; // Default railway car emoji
  }
};

function App() {
  const [simulationState, setSimulationState] = useState({ trains: [], simulation_time: "00:00:00" });

  useEffect(() => {
    const intervalId = setInterval(() => {
      fetch('http://127.0.0.1:5001/api/get_simulation_state')
        .then(response => response.json())
        .then(data => setSimulationState(data))
        .catch(error => console.error("Error fetching simulation state:", error));
    }, 2000); 

    return () => clearInterval(intervalId);
  }, []);

  // --- THIS IS THE CORRECTED POSITIONING LOGIC ---
  const getTrainPosition = (positionKm) => {
    // These constants are from our SVG's internal coordinate system (viewBox)
    const SVG_WIDTH = 1400;
    const TRACK_START_X = 210;
    const TRACK_END_X = 1310;

    // This must match the backend's route length
    const ROUTE_LENGTH_KM = 192;

    // 1. Calculate the percentage of the route the train has traveled
    let percentageTraveled = positionKm / ROUTE_LENGTH_KM;
    if (percentageTraveled > 1) percentageTraveled = 1;
    if (percentageTraveled < 0) percentageTraveled = 0;

    // 2. Calculate the width of the track itself within the SVG viewBox
    const trackWidthInSvg = TRACK_END_X - TRACK_START_X;

    // 3. Find the train's position along the track in SVG units
    const trainPositionInSvg = TRACK_START_X + (percentageTraveled * trackWidthInSvg);

    // 4. Convert the absolute SVG position to a percentage of the total SVG width
    const finalPercentage = (trainPositionInSvg / SVG_WIDTH) * 100;

    const yPos = 151; // The y-coordinate is constant

    // Return the position as a percentage string for CSS
    return { left: `${finalPercentage}%`, top: `${yPos}px` };
  };

  return (
    <div className="App">
      <h1>AI Train Traffic Control</h1>
      <h2>Simulation Time: {simulationState.simulation_time}</h2>
      <div className="dashboard">
        <TrainMap />
        {simulationState.trains.map(train => {
          const position = getTrainPosition(train.position_km);
          return (
            <div 
              key={train.id} 
              className="train-icon" 
              style={{ left: position.left, top: position.top }} // Use the percentage-based position
              title={`${train.name} (${train.id}) - Status: ${train.status}`}
            >
              {getTrainIcon(train.type)}
              <div className="train-label">{train.id}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
